                Export-Csv "$BackupPath\SystemConfig\EnabledWindowsFeatures.csv" -NoTypeInformation
            Write-Host "‚úì Backed up: Windows features list" -ForegroundColor Green
        } catch {
            Write-Host "‚úó Failed to backup Windows features list" -ForegroundColor Red
        }
        
        # Export firewall settings
        try {
