            )
            "Sysinternals" = @(
                "$env:USERPROFILE\Documents\Sysinternals"
            )
            
            # Archive Tools
            "7Zip" = @(
                "$env:APPDATA\7-Zip"
            )
            
            # AI/LLM Tools
            "Ollama" = @(
