        } catch {
            Write-Host "‚úó Failed to backup installed programs list" -ForegroundColor Red
        }
        
        # Export startup programs
        try {
            Get-CimInstance Win32_StartupCommand | 
                Select-Object Name, Command, Location, User | 
                Export-Csv "$BackupPath\SystemConfig\StartupPrograms.csv" -NoTypeInformation
            Write-Host "‚úì Backed up: Startup programs list" -ForegroundColor Green
        } catch {
            Write-Host "‚úó Failed to backup startup programs list" -ForegroundColor Red
        }
        
        # Export environment variables
        try {
            Get-ChildItem Env: | 
                Export-Csv "$BackupPath\SystemConfig\EnvironmentVariables.csv" -NoTypeInformation
            Write-Host "‚úì Backed up: Environment variables" -ForegroundColor Green
        } catch {
            Write-Host "‚úó Failed to backup environment variables" -ForegroundColor Red
        }
        
        # Export Windows features
        try {
            Get-WindowsOptionalFeature -Online | 
                Where-Object State -eq "Enabled" | 
                Export-Csv "$BackupPath\SystemConfig\EnabledWindowsFeatures.csv" -NoTypeInformation
            Write-Host "‚úì Backed up: Windows features list" -ForegroundColor Green
        } catch {
            Write-Host "‚úó Failed to backup Windows features list" -ForegroundColor Red
        }
        
        # Export firewall settings
        try {
            netsh advfirewall export "$BackupPath\SystemConfig\firewall.wfw"
            Write-Host "‚úì Backed up: Firewall settings" -ForegroundColor Green
        } catch {
            Write-Host "‚úó Failed to backup firewall settings" -ForegroundColor Red
        }
        
        # Export WiFi profiles
        try {
            netsh wlan export profile folder="$BackupPath\SystemConfig\" key=clear
            Write-Host "‚úì Backed up: WiFi profiles" -ForegroundColor Green
        } catch {
            Write-Host "‚úó Failed to backup WiFi profiles" -ForegroundColor Red
        }
        
        # ===================================================================
        # CREATE RESTORATION SCRIPT
        # ===================================================================
        Write-Host "`nCreating restoration script..." -ForegroundColor Yellow
        
        $restoreScript = @"
# Software Configuration Restoration Script
# Generated on: $(Get-Date)
# Backup Location: $BackupPath

Write-Host "Software Configuration Restoration Script" -ForegroundColor Cyan
Write-Host "=========================================" -ForegroundColor Cyan

# Registry Restoration
Write-Host "Restoring registry configurations..." -ForegroundColor Yellow
Get-ChildItem "$BackupPath\Registry\*.reg" | ForEach-Object {
    try {
