            Write-Host "‚úì Backed up: Installed programs list" -ForegroundColor Green
        } catch {
            Write-Host "‚úó Failed to backup installed programs list" -ForegroundColor Red
        }
        
        # Export startup programs
        try {
            Get-CimInstance Win32_StartupCommand | 
                Select-Object Name, Command, Location, User | 
                Export-Csv "$BackupPath\SystemConfig\StartupPrograms.csv" -NoTypeInformation
            Write-Host "‚úì Backed up: Startup programs list" -ForegroundColor Green
        } catch {
            Write-Host "‚úó Failed to backup startup programs list" -ForegroundColor Red
        }
        
        # Export environment variables
        try {
            Get-ChildItem Env: | 
