            )
            
            # Communication
            "WhatsApp" = @(
                "$env:LOCALAPPDATA\WhatsApp"
            )
            "Messenger" = @(
                "$env:LOCALAPPDATA\Facebook\Messenger"
            )
            
            # Security
            "Bitwarden" = @(
                "$env:APPDATA\Bitwarden"
            )
            
            # Network Tools
            "PuTTY" = @(
                "$env:APPDATA\PuTTY",
                "$env:USERPROFILE\.putty"
            )
            "WinSCP" = @(
                "$env:APPDATA\WinSCP.ini"
            )
            "Wireshark" = @(
