        } catch {
            Write-Host "‚úó Failed to backup hosts file" -ForegroundColor Red
        }
        
        # Export installed programs list
        try {
            Get-ItemProperty HKLM:\Software\Microsoft\Windows\CurrentVersion\Uninstall\* | 
                Select-Object DisplayName, DisplayVersion, Publisher, InstallDate | 
                Export-Csv "$BackupPath\SystemConfig\InstalledPrograms.csv" -NoTypeInformation
            Write-Host "‚úì Backed up: Installed programs list" -ForegroundColor Green
        } catch {
            Write-Host "‚úó Failed to backup installed programs list" -ForegroundColor Red
        }
        
        # Export startup programs
        try {
